mapper.update:
UPDATE member
SET password = #{password},
    name = #{name},
    email = #{email}
WHERE id = #{id}

mapper.select:
SELECT id, username, password, name, email, created_at as createdAt
FROM member
WHERE id = #{id}

SELECT id, username, password, name, email, created_at as createdAt
FROM member
WHERE username = #{username}
